import type { IsSupportedType } from '@/scopes/wrappers/wrapSafe.js';

export function createWrapSupported(
  component: string,
  isSupported: IsSupportedType,
): SafeWrapFn<true> {
  return createWrapSafe(component, { isSupported });
}

import { off, on, type EventListener } from '@telegram-apps/bridge';
import { getStorageValue, setStorageValue } from '@telegram-apps/toolkit';
import { isPageReload } from '@telegram-apps/navigation';

import { createSignalsTuple } from '@/signals-registry.js';
import { postEvent } from '@/globals.js';
import { createIsSupported } from '@/scopes/createIsSupported.js';
import { createWrapComplete } from '@/scopes/wrappers/createWrapComplete.js';
import { createWrapSupported } from '@/scopes/wrappers/createWrapSupported.js';

type StorageValue = boolean;

const SETUP_METHOD_NAME = 'web_app_setup_back_button';
const CLICK_EVENT_NAME = 'back_button_pressed';
const COMPONENT_NAME = 'backButton';

/**
 * Signal indicating if the Back Button is currently visible.
 */
export const [_isVisible, isVisible] = createSignalsTuple(false);

/**
 * Signal indicating if the Back Button is currently mounted.
 */
export const [_isMounted, isMounted] = createSignalsTuple(false);

/**
 * Signal indicating if the Back Button is supported.
 */
export const isSupported = createIsSupported(SETUP_METHOD_NAME);

const wrapComplete = createWrapComplete(COMPONENT_NAME, _isMounted, SETUP_METHOD_NAME);
const wrapSupported = createWrapSupported(COMPONENT_NAME, SETUP_METHOD_NAME);

/**
 * Hides the Back Button.
 * @param
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @throws {FunctionNotAvailableError} The parent component is not mounted
 * @since Mini Apps v6.1
 * @example
 * if (hide.isAvailable()) {
 *   hide();
 * }
 */
export const hide = wrapComplete('hide', (): void => {
  setVisibility(false);
});

/**
 * Mounts the Back Button restoring its state.
 * @param
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @since Mini Apps v6.1
 * @example
 * if (mount.isAvailable()) {
 *   mount();
 * }
 */
export const mount = wrapSupported('mount', (): void => {
  if (!_isMounted()) {
    setVisibility(isPageReload() && getStorageValue<StorageValue>(COMPONENT_NAME) || false);
    _isMounted.set(true);
  }
});

function setVisibility(value: boolean): void {
  if (value !== _isVisible()) {
    postEvent(SETUP_METHOD_NAME, { is_visible: value });
    setStorageValue<StorageValue>(COMPONENT_NAME, value);
    _isVisible.set(value);
  }
}

/**
 * Adds a new Back Button click listener.
 * @param fn - event listener.
 * @returns A function to remove bound listener.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @since Mini Apps v6.1
 * @example
 * if (onClick.isAvailable()) {
 *   const off = onClick(() => {
 *     console.log('User clicked the Back Button');
 *     off();
 *   });
 * }
 */
export const onClick = wrapSupported(
  'onClick',
  (fn: EventListener<'back_button_pressed'>): VoidFunction => on(CLICK_EVENT_NAME, fn),
);

/**
 * Removes the Back Button click listener.
 * @param fn - an event listener.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @since Mini Apps v6.1
 * @example
 * if (offClick.isAvailable()) {
 *   function listener() {
 *     console.log('User clicked the Back Button');
 *     offClick(listener);
 *   }
 *   onClick(listener);
 * }
 */
export const offClick = wrapSupported(
  'offClick',
  (fn: EventListener<'back_button_pressed'>): void => {
    off(CLICK_EVENT_NAME, fn);
  },
);

/**
 * Shows the Back Button.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @throws {FunctionNotAvailableError} The parent component is not mounted
 * @since Mini Apps v6.1
 * @example
 * if (show.isAvailable()) {
 *   show();
 * }
 */
export const show = wrapComplete('show', (): void => {
  setVisibility(true);
});

/**
 * Unmounts the Back Button.
 *
 * Note that this function does not remove listeners added via the `onClick`
 * function, so you have to remove them on your own.
 * @see onClick
 */
export function unmount(): void {
  _isMounted.set(false);
}

import { AbortablePromise } from 'better-promises';
import {
  batch,
  type Computed,
  type Signal,
} from '@telegram-apps/signals';
import { createComputed, createSignalsTuple, type SignalsTuple } from '@/signals-registry.js';
import { ConcurrentCallError } from '@/errors.js';

export function defineNonConcurrentFn<Fn extends (...args: any) => AbortablePromise<any>>(
  fn: Fn,
  errorMessage: string,
  options?: {
    /**
     * A signal with the promise to use instead of the generated one.
     */
    promise?: Signal<AbortablePromise<Awaited<ReturnType<Fn>>> | undefined>;
    /**
     * A signal with the error to use instead of the generated one.
     */
    error?: Signal<Error | undefined>;
  },
): [
  fn: Fn,
  promise: [
    ...SignalsTuple<AbortablePromise<Awaited<ReturnType<Fn>>> | undefined>,
    isRequesting: Computed<boolean>,
  ],
  error: SignalsTuple<Error | undefined>
] {
  options ||= {};
  const {
    promise: optionsPromise,
    error: optionsError,
  } = options;
  const [_promise, promise] =
    optionsPromise
      ? [optionsPromise, createComputed(optionsPromise)]
      : createSignalsTuple<AbortablePromise<Awaited<ReturnType<Fn>>> | undefined>();
  const [_error, error] =
    optionsError
      ? [optionsError, createComputed(optionsError)]
      : createSignalsTuple<Error | undefined>();

  return [
    Object.assign((...args: Parameters<Fn>): AbortablePromise<Awaited<ReturnType<Fn>>> => {
      if (_promise()) {
        const err = new ConcurrentCallError(errorMessage);
        _error.set(err);
        return AbortablePromise.reject(err);
      }

      batch(() => {
        _promise.set(fn(...args));
        _error.set(undefined);
      });

      let error: Error | undefined;
      return _promise()!
        .catch(e => {
          error = e;
          throw e;
        })
        .finally(() => {
          batch(() => {
            _promise.set(undefined);
            _error.set(error);
          });
        });
    }, fn),
    [_promise, promise, createComputed(() => !!_promise())],
    [_error, error],
  ];
}

import { batch, type Computed } from '@telegram-apps/signals';
import { AbortablePromise } from 'better-promises';

import { defineNonConcurrentFn } from '@/scopes/defineNonConcurrentFn.js';
import { createSignalsTuple, type SignalsTuple } from '@/signals-registry.js';

/**
 * Creates a mount function for a component.
 * @param component - the component name
 * @param mount - function mounting the component
 * @param onMounted - function that will be called whenever mount was completed.
 */
// #__NO_SIDE_EFFECTS__
export function defineMountFn<Fn extends (...args: any) => AbortablePromise<any>>(
  component: string,
  mount: Fn,
  onMounted: (result: Awaited<ReturnType<Fn>>) => void,
): [
  fn: (...args: Parameters<Fn>) => AbortablePromise<void>,
  promise: [
    ...SignalsTuple<AbortablePromise<Awaited<ReturnType<Fn>>> | undefined>,
    isRequesting: Computed<boolean>,
  ],
  error: SignalsTuple<Error | undefined>,
  isMounted: SignalsTuple<boolean>,
] {
  const [fn, ...rest] =
    defineNonConcurrentFn(mount, `The ${component} component is already mounting`);
  const [_isMounted, isMounted] = createSignalsTuple(false);

  return [
    (...args) => _isMounted()
      ? AbortablePromise.resolve()
      : fn(...args).then(data => {
        batch(() => {
          _isMounted.set(true);
          onMounted(data);
        });
      }),
    ...rest,
    [_isMounted, isMounted],
  ];
}

