import { isCancelledError } from 'better-promises';

/**
 * Throw the value if is not CanceledError.
 * @param e - value to check.
 */
export function ignoreCanceled(e: unknown): never | void {
  if (!isCancelledError(e)) {
    throw e;
  }
}

import type { AbortablePromise } from 'better-promises';
import { ignoreCanceled } from '@/utils/ignoreCanceled.js';

/**
 * Cancels the promise stored in the signal.
 * @param signal - signal with promise.
 */
export function signalCancel(signal: () => (AbortablePromise<any> | undefined)): void {
  const p = signal();
  p && p.catch(ignoreCanceled).cancel();
}

import {
  on,
  off,
  type BiometryTokenUpdateStatus,
  type BiometryAuthRequestStatus,
  type EventListener,
  type EventPayload,
} from '@telegram-apps/bridge';
