// #__NO_SIDE_EFFECTS__
export function rgbComputedBasedOn(signal: Computed<AnyColor>): Computed<RGB | undefined> {
  return createComputed<RGB | undefined>(() => rgbBasedOn(signal()));
}

export function rgbBasedOn(color: AnyColor): RGB | undefined {
  return isRGB(color) ? color : themeParamsState()[color];
}

import { isRGB } from '@telegram-apps/transformers';
import type { RGB } from '@telegram-apps/types';

import { isColorDark } from '@/utils/isColorDark.js';
import {
  secondaryBackgroundColor as themeSecondaryBgColor,
  state as themeParamsState,
} from '@/scopes/components/theme-params/signals.js';
import { createComputed, createSignalsTuple } from '@/signals-registry.js';
import { rgbComputedBasedOn } from './utils.js';

import type { AnyColor, State } from './types.js';

/**
 * The Mini App background color.
 *
 * Represents an RGB color, or theme parameters key, like "bg_color", "secondary_bg_color", etc.
 *
 * Note that using a theme parameters key, background color becomes bound to the current
 * theme parameters, making it automatically being updated whenever theme parameters change.
 * In order to remove this bind, use an explicit RGB color.
 */
export const [_backgroundColor, backgroundColor] = createSignalsTuple<AnyColor>('bg_color');

/**
 * RGB representation of the background color.
 *
 * This value requires the Theme Params component to be mounted to extract a valid RGB value
 * of the color key.
 */
export const backgroundColorRGB = rgbComputedBasedOn(_backgroundColor);


/**
 * The Mini App bottom bar background color.
 */
export const [_bottomBarColor, bottomBarColor] = createSignalsTuple<AnyColor>('bottom_bar_bg_color');

/**
 * RGB representation of the bottom bar background color.
 *
 * This value requires the Theme Params component to be mounted to extract a valid RGB value
 * of the color key.
 */
export const bottomBarColorRGB = createComputed<RGB | undefined>(() => {
  const color = _bottomBarColor();
  return isRGB(color)
    ? color
    // Falling back to secondary_bg_color following the logic from the Telegram SDK.
    : themeParamsState()[color] || themeSecondaryBgColor();
});

/**
 * The Mini App header color.
 */
export const [_headerColor, headerColor] = createSignalsTuple<AnyColor>('bg_color');

/**
 * RGB representation of the header color.
 *
 * This value requires the Theme Params component to be mounted to extract a valid RGB value
 * of the color key.
 */
export const headerColorRGB = rgbComputedBasedOn(_headerColor);

/**
 * True if CSS variables are currently bound.
 */
export const [_isCssVarsBound, isCssVarsBound] = createSignalsTuple(false);

/**
 * True if the current Mini App background color is recognized as dark.
 */
export const isDark = createComputed(() => {
  const color = backgroundColorRGB();
  return color ? isColorDark(color) : false;
});

/**
 * Signal indicating if the mini app is currently active.
 */
export const [_isActive, isActive] = createSignalsTuple(true);

/**
 * Complete component state.
 */
export const state = createComputed<State>(() => ({
  backgroundColor: _backgroundColor(),
  bottomBarColor: _bottomBarColor(),
  headerColor: _headerColor(),
  isActive: _isActive(),
}));

import {
  supports,
  on,
  off,
  type EventListener,
  type MethodName,
} from '@telegram-apps/bridge';
import { isRGB } from '@telegram-apps/transformers';
import { isPageReload } from '@telegram-apps/navigation';
import { type Computed, type SubscribeListenerFn, batch } from '@telegram-apps/signals';
import {
  camelToKebab,
  createCbCollector,
  getStorageValue,
  setStorageValue,
} from '@telegram-apps/toolkit';
import type { RGB, ThemeParams } from '@telegram-apps/types';

import { version, postEvent } from '@/globals.js';
import {
  mount as mountThemeParams,
  mountSync as mountThemeParamsSync,
} from '@/scopes/components/theme-params/methods.js';
import { createWrapComplete } from '@/scopes/wrappers/createWrapComplete.js';
import { createWrapSupported } from '@/scopes/wrappers/createWrapSupported.js';
import { createWrapBasic } from '@/scopes/wrappers/createWrapBasic.js';
import { createComputed } from '@/signals-registry.js';
import { CSSVarsBoundError, UnknownThemeParamsKeyError } from '@/errors.js';
import { deleteCssVar, setCssVar } from '@/utils/css-vars.js';
import { defineMountFn } from '@/scopes/defineMountFn.js';
import { signalCancel } from '@/scopes/signalCancel.js';
import type { RequestOptionsNoCapture } from '@/types.js';

import {
  _isCssVarsBound,
  state,
  headerColorRGB,
  bottomBarColorRGB,
  backgroundColorRGB,
  _isActive,
  _backgroundColor,
  _bottomBarColor,
  _headerColor,
} from './signals.js';
import type { AnyColor, GetCssVarNameFn, State } from './types.js';
import { rgbBasedOn } from './utils.js';
import { themeParamsState } from '@/scopes/components/theme-params/exports.js';

type StorageValue = State;

const SET_BG_COLOR_METHOD = 'web_app_set_background_color';
const SET_BOTTOM_BAR_COLOR_METHOD = 'web_app_set_bottom_bar_color';
const SET_HEADER_COLOR_METHOD = 'web_app_set_header_color';
const VISIBILITY_CHANGED_EVENT = 'visibility_changed';
const COMPONENT_NAME = 'miniApp';

const isSupportedSchema = {
  any: [
    SET_BG_COLOR_METHOD,
    SET_BOTTOM_BAR_COLOR_METHOD,
    SET_HEADER_COLOR_METHOD,
  ] as MethodName[],
};

/**
 * True if the Mini App component is supported.
 */
export const isSupported = createComputed(() => {
  return isSupportedSchema.any.some(method => supports(method, version()));
});

const onVisibilityChanged: EventListener<'visibility_changed'> = (data) => {
  _isActive.set(data.is_visible);
  saveState();
};

const onThemeParamsChanged: SubscribeListenerFn<ThemeParams> = themeParams => {
  ([
    [_headerColor, SET_HEADER_COLOR_METHOD],
    [_backgroundColor, SET_BG_COLOR_METHOD],
    [_bottomBarColor, SET_BOTTOM_BAR_COLOR_METHOD],
  ] as const).forEach(([signal, method]) => {
    const color = signal();
    if (!isRGB(color) && (
      // Header color setter uses additional checks. We don't apply changes if the current
      // value is a known color key because it updates automatically by itself.
      method !== SET_HEADER_COLOR_METHOD
      || (color !== 'bg_color' && color !== 'secondary_bg_color')
    )) {
      const rgb = themeParams[color];
      rgb && postEvent(method, { color: rgb });
    }
  });
};

const [
  mountFn,
  tMountPromise,
  tMountError,
  tIsMounted,
] = defineMountFn(
  COMPONENT_NAME,
  (options?: RequestOptionsNoCapture) => {
    return mountThemeParams(options).then(() => {
      return isPageReload() && getStorageValue<StorageValue>(COMPONENT_NAME) || undefined;
    });
  },
  s => {
    setBackgroundColor.ifAvailable(s ? s.backgroundColor : 'bg_color');
    setBottomBarColor.ifAvailable(s ? s.bottomBarColor : 'bottom_bar_bg_color');
    setHeaderColor.ifAvailable(s ? s.headerColor : 'bg_color');
    _isActive.set(s ? s.isActive : true);

    on(VISIBILITY_CHANGED_EVENT, onVisibilityChanged);
    themeParamsState.sub(onThemeParamsChanged);
  },
);

const wrapBasic = createWrapBasic(COMPONENT_NAME);
const wrapSupported = createWrapSupported(COMPONENT_NAME, isSupportedSchema);
const wrapComplete = createWrapComplete(COMPONENT_NAME, tIsMounted[0], isSupportedSchema);

/**
 * Creates CSS variables connected with the mini app.
 *
 * Default variables:
 * - `--tg-bg-color`
 * - `--tg-header-color`
 * - `--tg-bottom-bar-color`
 *
 * Variables are being automatically updated if theme parameters were changed.
 *
 * @param getCSSVarName - function, returning complete CSS variable name for the specified
 * mini app key.
 * @returns Function to stop updating variables.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {CSSVarsBoundError} CSS variables are already bound
 * @throws {FunctionNotAvailableError} The parent component is not mounted
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @example Using no arguments
 * if (bindCssVars.isAvailable()) {
 *   bindCssVars();
 * }
 * @example Using custom CSS vars generator
 * if (bindCssVars.isAvailable()) {
 *   bindCssVars(key => `--my-prefix-${key}`);
 * }
 */
export const bindCssVars = wrapComplete(
  'bindCssVars',
  (getCSSVarName?: GetCssVarNameFn): VoidFunction => {
    if (_isCssVarsBound()) {
      throw new CSSVarsBoundError();
    }

    const [addCleanup, cleanup] = createCbCollector();

    /**
     * Binds specified CSS variable to a signal.
     * @param cssVar - CSS variable name.
     * @param signal - signal to listen changes to.
     */
    function bind(cssVar: string, signal: Computed<RGB | undefined>) {
      function update() {
        setCssVar(cssVar, signal() || null);
      }

      // Instantly set CSS variable.
      update();

      // Remember to clean this relation up.
      addCleanup(signal.sub(update), deleteCssVar.bind(null, cssVar));
    }

    getCSSVarName ||= (prop) => `--tg-${camelToKebab(prop)}`;
    bind(getCSSVarName('bgColor'), backgroundColorRGB);
    bind(getCSSVarName('bottomBarColor'), bottomBarColorRGB);
    bind(getCSSVarName('headerColor'), headerColorRGB);
    addCleanup(() => {
      _isCssVarsBound.set(false);
    });

    _isCssVarsBound.set(true);

    return cleanup;
  },
);

/**
 * Closes the Mini App.
 * @param returnBack - should the client return to the previous activity.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @example
 * if (close.isAvailable()) {
 *   close();
 * }
 */
export const close = wrapBasic('close', (returnBack?: boolean): void => {
  postEvent('web_app_close', { return_back: returnBack });
});

/**
 * Mounts the component.
 *
 * This function restores the component state and is automatically saving it in the local storage
 * if it changed.
 *
 * Internally, the function mounts the Theme Params component to work with correctly extracted
 * theme palette values.
 * @since Mini Apps v6.1
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @deprecated Use `mountSync`.
 * @example
 * if (mount.isAvailable()) {
 *   await mount();
 * }
 */
export const mount = wrapSupported('mount', mountFn);
/**
 * @deprecated The initialization is synchronous. Use `mountSync`.
 */
export const isMounting = tMountPromise[2];
/**
 * @deprecated The initialization is synchronous. Use `mountSync`.
 */
export const mountPromise = tMountPromise[1];
/**
 * @deprecated The initialization is synchronous. Use `mountSync`.
 */
export const mountError = tMountError[1];

export const [_isMounted, isMounted] = tIsMounted;

/**
 * Mounts the component.
 *
 * This function restores the component state and is automatically saving it in the local storage
 * if it changed.
 *
 * Internally, the function mounts the Theme Params component to work with correctly extracted
 * theme palette values.
 * @since Mini Apps v6.1
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @example
 * if (mountSync.isAvailable()) {
 *   mountSync();
 * }
 */
export const mountSync = wrapSupported('mountSync', () => {
  if (!_isMounted()) {
    mountThemeParamsSync();
    const s = isPageReload() && getStorageValue<StorageValue>(COMPONENT_NAME) || undefined;

    setBackgroundColor.ifAvailable(s ? s.backgroundColor : 'bg_color');
    setBottomBarColor.ifAvailable(s ? s.bottomBarColor : 'bottom_bar_bg_color');
    setHeaderColor.ifAvailable(s ? s.headerColor : 'bg_color');
    on(VISIBILITY_CHANGED_EVENT, onVisibilityChanged);
    themeParamsState.sub(onThemeParamsChanged);

    batch(() => {
      _isActive.set(s ? s.isActive : true);
      _isMounted.set(true);
    });
  }
});

/**
 * Informs the Telegram app that the Mini App is ready to be displayed.
 *
 * It is recommended to call this method as early as possible, as soon as all
 * essential interface elements loaded.
 *
 * Once this method is called, the loading placeholder is hidden and the Mini
 * App shown.
 *
 * If the method is not called, the placeholder will be hidden only when the
 * page was fully loaded.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @example
 * if (ready.isAvailable()) {
 *   ready();
 * }
 */
export const ready = wrapBasic('ready', (): void => {
  postEvent('web_app_ready');
});

function saveState() {
  setStorageValue<StorageValue>(COMPONENT_NAME, state());
}

/**
 * Updates the background color.
 * @since Mini Apps v6.1
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @throws {FunctionNotAvailableError} The parent component is not mounted
 * @throws {UnknownThemeParamsKeyError} Unknown theme params key passed
 * @example
 * if (setBackgroundColor.isAvailable()) {
 *   setBackgroundColor('bg_color');
 * }
 */
export const setBackgroundColor = wrapComplete(
  'setBackgroundColor',
  (color: AnyColor): void => {
    if (color === _backgroundColor()) {
      return;
    }
    const rgb = rgbBasedOn(color);
    if (!rgb) {
      throw new UnknownThemeParamsKeyError(color);
    }
    postEvent(SET_BG_COLOR_METHOD, { color: rgb });
    _backgroundColor.set(color);
    saveState();
  },
  SET_BG_COLOR_METHOD,
);

/**
 * Updates the bottom bar background color.
 * @since Mini Apps v7.10
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @throws {FunctionNotAvailableError} The parent component is not mounted
 * @throws {UnknownThemeParamsKeyError} Unknown theme params key passed
 * @example
 * if (setBottomBarColor.isAvailable()) {
 *   setBottomBarColor('#ff11a3');
 * }
 */
export const setBottomBarColor = wrapComplete(
  'setBottomBarColor',
  (color: AnyColor) => {
    if (color === _bottomBarColor()) {
      return;
    }
    const rgb = rgbBasedOn(color);
    if (!rgb) {
      throw new UnknownThemeParamsKeyError(color);
    }
    postEvent(SET_BOTTOM_BAR_COLOR_METHOD, { color: rgb });
    _bottomBarColor.set(color);
    saveState();
  },
  SET_BOTTOM_BAR_COLOR_METHOD,
);

/**
 * Updates the header color.
 * @since Mini Apps v6.1
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @throws {FunctionNotAvailableError} The parent component is not mounted
 * @throws {UnknownThemeParamsKeyError} Unknown theme params key passed
 * @example Using known color key
 * if (setHeaderColor.isAvailable()) {
 *   setHeaderColor('bg_color');
 * }
 * @example Using RGB
 * if (setHeaderColor.isAvailable() && setHeaderColor.supports.rgb()) {
 *   setHeaderColor('#ffaabb');
 * }
 */
export const setHeaderColor = wrapComplete(
  'setHeaderColor',
  (color: AnyColor): void => {
    if (color === _headerColor()) {
      return;
    }
    if (color === 'bg_color' || color === 'secondary_bg_color') {
      postEvent(SET_HEADER_COLOR_METHOD, { color_key: color });
    } else {
      const rgb = rgbBasedOn(color);
      if (!rgb) {
        throw new UnknownThemeParamsKeyError(color);
      }
      postEvent(SET_HEADER_COLOR_METHOD, { color: rgb });
    }
    _headerColor.set(color);
    saveState();
  },
  SET_HEADER_COLOR_METHOD,
  {
    rgb: [SET_HEADER_COLOR_METHOD, 'color', isRGB],
  },
);

/**
 * Unmounts the component, removing the listener, saving the component state in the local storage.
 */
export function unmount(): void {
  signalCancel(mountPromise);
  off(VISIBILITY_CHANGED_EVENT, onVisibilityChanged);
  themeParamsState.unsub(onThemeParamsChanged);
  _isMounted.set(false);
}

import { type PopupParams } from '@telegram-apps/bridge';

import { InvalidArgumentsError } from '@/errors.js';

import type { OpenOptions } from './types.js';

/**
 * Prepares popup parameters before sending them to native app.
 * @param params - popup parameters.
 * @throws {InvalidArgumentsError} Invalid title
 * @throws {InvalidArgumentsError} Invalid message
 * @throws {InvalidArgumentsError} Invalid buttons count
 * @throws {InvalidArgumentsError} Invalid button id length
 * @throws {InvalidArgumentsError} Invalid button text length
 */
export function prepareParams(params: OpenOptions): PopupParams {
  const message = params.message.trim();
  const title = (params.title || '').trim();
  const buttons = params.buttons || [];

  if (title.length > 64) {
    throw new InvalidArgumentsError(`Invalid title: ${title}`);
  }
  if (!message || message.length > 256) {
    throw new InvalidArgumentsError(`Invalid message: ${message}`);
  }
  if (buttons.length > 3) {
    throw new InvalidArgumentsError(`Invalid buttons count: ${buttons.length}`);
  }

  return {
    title,
    message,
    buttons: buttons.length
      ? buttons.map((b, idx) => {
        const id = b.id || '';
        if (id.length > 64) {
          throw new InvalidArgumentsError(`Button with index ${idx} has invalid id: ${id}`);
        }

        if (!b.type || b.type === 'default' || b.type === 'destructive') {
          const text = b.text.trim();
          if (!text || text.length > 64) {
            throw new InvalidArgumentsError(`Button with index ${idx} has invalid text: ${text}`);
          }
          return { type: b.type, text, id };
        }
        return { type: b.type, id };
      })
      : [{ type: 'close', id: '' }],
  };
}

import type { AbortablePromise } from 'better-promises';

import { request } from '@/globals.js';
import { createIsSupported } from '@/scopes/createIsSupported.js';
import { createWrapSupported } from '@/scopes/wrappers/createWrapSupported.js';
import { defineNonConcurrentFn } from '@/scopes/defineNonConcurrentFn.js';

import { prepareParams } from './prepareParams.js';
import type { ShowOptions } from './types.js';

const OPEN_METHOD = 'web_app_open_popup';
const wrapSupported = createWrapSupported('popup', OPEN_METHOD);

/**
 * Signal indicating if popups are supported.
 */
export const isSupported = createIsSupported(OPEN_METHOD);

const [fn, tPromise, tShowError] = defineNonConcurrentFn(
  (options: ShowOptions): AbortablePromise<string | null> => {
    return request(OPEN_METHOD, 'popup_closed', {
      ...options,
      params: prepareParams(options),
    }).then(({ button_id: buttonId }) => buttonId === undefined ? null : buttonId);
  },
  'A popup is already opened',
);

/**
 * @deprecated Deprecated for consistence naming, to be removed in the next major update. Use
 *   `show` instead.
 */
export const open = wrapSupported('open', fn);
/**
 * @deprecated Deprecated for consistence naming, to be removed in the next major update. Use
 *   `showPromise` instead.
 */
const openPromise = tPromise[1];
/**
 * @deprecated Deprecated for consistence naming, to be removed in the next major update. Use
 *   `isShown` instead.
 */
const isOpened = tPromise[2];
/**
 * @deprecated Deprecated for consistence naming, to be removed in the next major update. Use
 *   `showError` instead.
 */
const openError = tShowError[1];

export { openPromise, isOpened, openError };

/**
 * A method that shows a native popup described by the `params` argument.
 * The promise will be resolved when the popup is closed. Resolved value will have
 * an identifier of the pressed button.
 *
 * If a user clicked outside the popup or clicked the top right popup close
 * button, null will be resolved.
 *
 * @param options - popup parameters.
 * @since Mini Apps v6.2
 * @throws {InvalidArgumentsError} Invalid title
 * @throws {InvalidArgumentsError} Invalid message
 * @throws {InvalidArgumentsError} Invalid buttons count
 * @throws {InvalidArgumentsError} Invalid button id length
 * @throws {InvalidArgumentsError} Invalid button text length
 * @throws {ConcurrentCallError} A popup is already opened
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @example
 * if (open.isAvailable()) {
 *   const buttonId = await open({
 *     title: 'Confirm action',
 *     message: 'Do you really want to buy this burger?',
 *     buttons: [
 *       { id: 'yes', text: 'Yes' },
 *       { id: 'no', type: 'destructive', text: 'No' },
 *     ],
 *   });
 * }
 */
export const show = wrapSupported('show', fn);
export const [, showPromise, isShown] = tPromise;
export const [, showError] = tShowError;

import { on } from '@telegram-apps/bridge';
import { createCbCollector } from '@telegram-apps/toolkit';
import { AbortablePromise, ManualPromise } from 'better-promises';

import { postEvent } from '@/globals.js';
import { createWrapSupported } from '@/scopes/wrappers/createWrapSupported.js';
import { defineNonConcurrentFn } from '@/scopes/defineNonConcurrentFn.js';
import { createIsSupported } from '@/scopes/createIsSupported.js';
import { signalCancel } from '@/scopes/signalCancel.js';
import type { RequestOptionsNoCapture } from '@/types.js';
import { ignoreCanceled } from '@/utils/ignoreCanceled.js';

interface OpenSharedOptions extends RequestOptionsNoCapture {
  /**
   * Title to be displayed in the scanner.
   */
  text?: string;
}

const CLOSE_METHOD = 'web_app_close_scan_qr_popup';
const OPEN_METHOD = 'web_app_open_scan_qr_popup';
const CLOSED_EVENT = 'scan_qr_popup_closed';
const TEXT_RECEIVED_EVENT = 'qr_text_received';

const wrapSupported = createWrapSupported('qrScanner', OPEN_METHOD);

/**
 * Closes the scanner.
 * @since Mini Apps v6.4
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @example
 * if (close.isAvailable()) {
 *   close();
 * }
 */
export const close = wrapSupported('close', (): void => {
  postEvent(CLOSE_METHOD);
  signalCancel(openPromise);
});

/**
 * Signal indicating if the QR Scanner is currently opened.
 */
export const isSupported = createIsSupported(OPEN_METHOD);

/**
 * Opens the scanner and returns a promise which will be resolved with the QR content if the
 * passed `capture` function returned true.
 *
 * The `capture` option may be ommited. In this case, the first scanned QR will be returned.
 *
 * Promise may also be resolved to undefined if the scanner was closed.
 * @param options - method options.
 * @returns A promise with QR content presented as string or undefined if the
 * scanner was closed.
 * @since Mini Apps v6.4
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @throws {ConcurrentCallError} The QR Scanner is already opened
 * @example Without `capture` option
 * if (captureOne.isAvailable()) {
 *   const qr = await captureOne({ text: 'Scan any QR' });
 * }
 * @example Using `capture` option
 * if (captureOne.isAvailable()) {
 *   const qr = await captureOne({
 *     text: 'Scan any QR',
 *     capture(scannedQr) {
 *       return scannedQr === 'any expected by me qr';
 *     }
 *   });
 * }
 */
function _open(
  options?: OpenSharedOptions & {
    /**
     * Function, which should return true if the scanned QR should be captured.
     * @param qr - scanned QR content.
     */
    capture?: (qr: string) => boolean;
  },
): AbortablePromise<string | undefined>;

/**
 * Opens the scanner and calls the `onCaptured` function each time, a QR was scanned.
 *
 * The function returns a promise which will be resolved when the QR scanner was closed. It expects
 * the scanner to be closed externally by a user or via the `close` method.
 * @param options - method options.
 * @since Mini Apps v6.4
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @throws {ConcurrentCallError} The QR Scanner is already opened
 * @example
 * if (captureMany.isAvailable()) {
 *   const promise = await captureMany({
 *     text: 'Scan any QR',
 *     onCaptured(scannedQr) {
 *       if (scannedQr === 'any expected by me qr') {
 *         close();
 *       }
 *     }
 *   });
 *   console.log('The scanner was closed');
 * }
 */
function _open(
  options: OpenSharedOptions & {
    /**
     * Function which will be called if a QR code was scanned.
     * @param qr - scanned QR content.
     */
    onCaptured: (qr: string) => void;
  },
): AbortablePromise<void>;

function _open(
  options?: OpenSharedOptions & {
    onCaptured?: (qr: string) => void;
    capture?: (qr: string) => boolean;
  },
): AbortablePromise<string | undefined | void> {
  options ||= {};
  const { onCaptured, text, capture } = options;
  const [, cleanup] = createCbCollector(
    on(CLOSED_EVENT, () => {
      promise.resolve();
    }),
    on(TEXT_RECEIVED_EVENT, (event) => {
      if (onCaptured) {
        onCaptured(event.data);
      } else if (!capture || capture(event.data)) {
        promise.resolve(event.data);
        postEvent(CLOSE_METHOD);
      }
    }),
  );

  const promise = new ManualPromise<string | void>(options);
  (options.postEvent || postEvent)(OPEN_METHOD, { text });

  return AbortablePromise.resolve(promise).catch(ignoreCanceled).finally(cleanup);
}

const [
  openFn,
  tOpenPromise,
  tOpenError,
] = defineNonConcurrentFn(_open, 'The QR Scanner is already opened');

export const open = wrapSupported('open', openFn);
export const [, openPromise, isOpened] = tOpenPromise;
export const [, openError] = tOpenError;

import type { Computed } from '@telegram-apps/signals';

import { buttonColor } from '@/scopes/components/theme-params/signals.js';
import { bottomBarColorRGB } from '@/scopes/components/mini-app/signals.js';
import { createComputed, createSignal, createSignalsTuple } from '@/signals-registry.js';

import type { State } from './types.js';

function fromState<K extends keyof Required<State>>(key: K): Computed<Required<State>[K]> {
  return createComputed(() => state()[key]);
}

export const internalState = createSignal<State>({
  hasShineEffect: false,
  isEnabled: true,
  isLoaderVisible: false,
  isVisible: false,
  position: 'left',
  text: 'Cancel',
});

/**
 * Complete component state.
 */
export const state = createComputed<Required<State>>(() => {
  const s = internalState();
  return {
    ...s,
    backgroundColor: s.backgroundColor || bottomBarColorRGB() || '#000000',
    textColor: s.textColor || buttonColor() || '#2481cc',
  };
});

/**
 * True if the component is currently mounted.
 */
export const [_isMounted, isMounted] = createSignalsTuple(false);

/**
 * @see State.backgroundColor
 */
export const backgroundColor = fromState('backgroundColor');

/**
 * @see State.hasShineEffect
 */
export const hasShineEffect = fromState('hasShineEffect');

/**
 * @see State.isEnabled
 */
export const isEnabled = fromState('isEnabled');

/**
 * @see State.isLoaderVisible
 */
export const isLoaderVisible = fromState('isLoaderVisible');

/**
 * @see State.isVisible
 */
export const isVisible = fromState('isVisible');

/**
 * @see State.position
 */
export const position = fromState('position');

/**
 * @see State.text
 */
export const text = fromState('text');

/**
 * @see State.textColor
 */
export const textColor = fromState('textColor');

import {
  off,
  on,
  type EventListener,
} from '@telegram-apps/bridge';
import { isPageReload } from '@telegram-apps/navigation';
import { getStorageValue, setStorageValue } from '@telegram-apps/toolkit';

import { postEvent } from '@/globals.js';
import { createIsSupported } from '@/scopes/createIsSupported.js';
import { createWrapComplete } from '@/scopes/wrappers/createWrapComplete.js';
import { createWrapSupported } from '@/scopes/wrappers/createWrapSupported.js';

import { internalState, isMounted, _isMounted, state } from './signals.js';
import type { State } from './types.js';
import { removeUndefined } from '@/utils/removeUndefined.js';

type StorageValue = State;

const SETUP_METHOD_NAME = 'web_app_setup_secondary_button';
const CLICK_EVENT_NAME = 'secondary_button_pressed';
const COMPONENT_NAME = 'secondaryButton';

const wrapSupported = createWrapSupported(COMPONENT_NAME, SETUP_METHOD_NAME);
const wrapComplete = createWrapComplete(COMPONENT_NAME, isMounted, SETUP_METHOD_NAME);

/**
 * Signal indicating if the Secondary Button is supported.
 */
export const isSupported = createIsSupported(SETUP_METHOD_NAME);

/**
 * Mounts the Secondary Button restoring its state.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @example
 * if (mount.isAvailable()) {
 *   mount();
 * }
 */
export const mount = wrapSupported('mount', (): void => {
  if (!_isMounted()) {
    const prev = isPageReload() && getStorageValue<StorageValue>(COMPONENT_NAME);
    prev && internalState.set(prev);
    _isMounted.set(true);
  }
});

/**
 * Adds a new Secondary Button click listener.
 * @param fn - event listener.
 * @returns A function to remove bound listener.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @example
 * if (onClick.isAvailable()) {
 *   const off = onClick(() => {
 *     console.log('User clicked the Secondary Button');
 *     off();
 *   });
 * }
 */
export const onClick = wrapSupported(
  'onClick',
  (fn: EventListener<'secondary_button_pressed'>): VoidFunction => on(CLICK_EVENT_NAME, fn),
);

/**
 * Removes the Secondary Button click listener.
 * @param fn - an event listener.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @example
 * if (offClick.isAvailable()) {
 *   function listener() {
 *     console.log('User clicked the Secondary Button');
 *     offClick(listener);
 *   }
 *   onClick(listener);
 * }
 */
export const offClick = wrapSupported(
  'offClick',
  (fn: EventListener<'secondary_button_pressed'>): void => {
    off(CLICK_EVENT_NAME, fn);
  },
);

/**
 * Updates the Secondary Button state.
 * @param updates - state changes to perform.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The parent component is not mounted
 * @throws {FunctionNotAvailableError} The function is not supported
 * @example
 * if (setParams.isAvailable()) {
 *   setParams({
 *     text: 'Submit',
 *     isEnabled: true,
 *     isVisible: true,
 *     position: 'left',
 *   });
 * }
 */
export const setParams = wrapComplete(
  'setParams',
  (updates: Partial<State>): void => {
    internalState.set({ ...internalState(), ...removeUndefined(updates) });
    setStorageValue<StorageValue>(COMPONENT_NAME, internalState());

    // We should not commit changes until the payload is correct. Some version of Telegram will
    // crash due to the empty value of the text.
    const s = state();
    s.text && postEvent(SETUP_METHOD_NAME, {
      color: s.backgroundColor,
      has_shine_effect: s.hasShineEffect,
      is_active: s.isEnabled,
      is_progress_visible: s.isLoaderVisible,
      is_visible: s.isVisible,
      position: s.position,
      text: s.text,
      text_color: s.textColor,
    });
  },
);

/**
 * Unmounts the Secondary Button.
 *
 * Note that this function does not remove listeners added via the `onClick`
 * function, so you have to remove them on your own.
 * @see onClick
 */
export function unmount(): void {
  _isMounted.set(false);
}

