  _isMounted.set(false);
}

export const COMPONENT_NAME = 'viewport';
export const FS_CHANGED_EVENT = 'fullscreen_changed';
export const SA_CHANGED_EVENT = 'safe_area_changed';
export const CSA_CHANGED_EVENT = 'content_safe_area_changed';
export const VIEWPORT_CHANGED_EVENT = 'viewport_changed';

import { createWrapBasic } from '@/scopes/wrappers/createWrapBasic.js';

import { COMPONENT_NAME } from './const.js';

export const wrapBasic = createWrapBasic(COMPONENT_NAME);

import type { Computed } from '@telegram-apps/signals';
import type { SafeAreaInsets } from '@telegram-apps/bridge';
import { getStorageValue, setStorageValue } from '@telegram-apps/toolkit';

import { createComputed, createSignalsTuple } from '@/signals-registry.js';
import type { State } from '@/scopes/components/viewport/types.js';
import { removeUndefined } from '@/utils/removeUndefined.js';
import { COMPONENT_NAME } from '@/scopes/components/viewport/const.js';

const initialInsets: SafeAreaInsets = { left: 0, top: 0, bottom: 0, right: 0 };

function nonNegative(value: number): number {
  return Math.max(value, 0);
}

//#region Core State.

/**
 * Signal containing the component complete state.
 */
export const [_state, state] = createSignalsTuple<State>({
  contentSafeAreaInsets: initialInsets,
  height: 0,
  isExpanded: false,
  isFullscreen: false,
  safeAreaInsets: initialInsets,
  stableHeight: 0,
  width: 0,
});

export function signalFromState<K extends keyof State>(key: K): Computed<State[K]> {
  return createComputed(() => state()[key]);
}

/**
 * Signal containing the current height of the **visible area** of the Mini App.
 *
 * The application can display just the top part of the Mini App, with its
 * lower part remaining outside the screen area. From this position, the user
 * can "pull" the Mini App to its maximum height, while the bot can do the same
 * by calling `expand` method. As the position of the Mini App changes, the
 * current height value of the visible area will be updated  in real time.
 *
 * Please note that the refresh rate of this value is not sufficient to
 * smoothly follow the lower border of the window. It should not be used to pin
 * interface elements to the bottom of the visible area. It's more appropriate
 * to use the value of the `stableHeight` field for this purpose.
 *
 * @see stableHeight
 */
export const height = signalFromState('height');

/**
 * Signal containing the height of the visible area of the Mini App in its last stable state.
 *
 * The application can display just the top part of the Mini App, with its
 * lower part remaining outside the screen area. From this position, the user
 * can "pull" the Mini App to its maximum height, while the application can do
 * the same by calling `expand` method.
 *
 * Unlike the value of `height`, the value of `stableHeight` does not change as
 * the position of the Mini App changes with user gestures or during
 * animations. The value of `stableHeight` will be updated after all gestures
 * and animations are completed and the Mini App reaches its final size.
 *
 * @see height
 */
export const stableHeight = signalFromState('stableHeight');

/**
 * Signal containing the currently visible area width.
 */
export const width = signalFromState('width');

/**
 * Signal indicating if the Mini App is expanded to the maximum available height. Otherwise,
 * if the Mini App occupies part of the screen and can be expanded to the full
 * height using `expand` method.
 * @see expand
 */
export const isExpanded = signalFromState('isExpanded');

/**
 * Signal indicating if the current viewport height is stable and is not going to change in
 * the next moment.
 */
export const isStable = createComputed(() => height() === stableHeight());

/**
 * Updates the viewport signal state saving it in the storage.
 * @param s - state updates.
 */
export function setState(s: Partial<State>): void {
  const { height, stableHeight, width } = s;

  _state.set({
    ..._state(),
    ...removeUndefined({
      ...s,
      height: height ? nonNegative(height) : undefined,
      width: width ? nonNegative(width) : undefined,
      stableHeight: stableHeight ? nonNegative(stableHeight) : undefined,
    }),
  });
  setStorageValue<State>(COMPONENT_NAME, _state());
}

/**
 * Retrieves the viewport state from the storage.
 */
export function getStateFromStorage(): State | undefined {
  return getStorageValue<State>(COMPONENT_NAME);
}

//#endregion

//#region Content Safe Area Insets.

function fromCsaState(key: keyof SafeAreaInsets): Computed<number> {
  return createComputed(() => contentSafeAreaInsets()[key]);
}

export const contentSafeAreaInsets = signalFromState('contentSafeAreaInsets');
export const contentSafeAreaInsetBottom = fromCsaState('bottom');
export const contentSafeAreaInsetLeft = fromCsaState('left');
export const contentSafeAreaInsetRight = fromCsaState('right');
export const contentSafeAreaInsetTop = fromCsaState('top');

//#endregion

//#region Safe Area Insets.

function fromSaState(key: keyof SafeAreaInsets): Computed<number> {
  return createComputed(() => safeAreaInsets()[key]);
}

export const safeAreaInsets = signalFromState('safeAreaInsets');
export const safeAreaInsetBottom = fromSaState('bottom');
export const safeAreaInsetLeft = fromSaState('left');
export const safeAreaInsetRight = fromSaState('right');
export const safeAreaInsetTop = fromSaState('top');

//#endregion

import type { AbortablePromise } from 'better-promises';
import type { EventPayload } from '@telegram-apps/bridge';

import { request } from '@/globals.js';
import { createWrapSupported } from '@/scopes/wrappers/createWrapSupported.js';
import type { RequestOptionsNoCapture } from '@/types.js';

import {
  COMPONENT_NAME,
  CSA_CHANGED_EVENT,
  SA_CHANGED_EVENT,
  VIEWPORT_CHANGED_EVENT,
} from './const.js';

const REQUEST_SA_METHOD_NAME = 'web_app_request_safe_area';
const wrapSafe = createWrapSupported(COMPONENT_NAME, REQUEST_SA_METHOD_NAME);

/**
 * Requests the actual viewport content safe area insets information.
 * @param options - request options.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @since Mini Apps v8.0
 * @example
 * if (requestContentSafeAreaInsets.isAvailable()) {
 *   const insets = await requestContentSafeAreaInsets();
 * }
 */
export const requestContentSafeAreaInsets = wrapSafe(
  'requestContentSafeAreaInsets',
  (options?: RequestOptionsNoCapture): AbortablePromise<EventPayload<'content_safe_area_changed'>> => {
    return request('web_app_request_content_safe_area', CSA_CHANGED_EVENT, options);
  },
);

/**
 * Requests viewport actual information from the Telegram application.
 * @param options - request options.
 * @example
 * if (requestViewport.isAvailable()) {
 *   const viewport = await requestViewport();
 * }
 */
export function requestViewport(
  options?: RequestOptionsNoCapture,
): AbortablePromise<EventPayload<'viewport_changed'>> {
  return request('web_app_request_viewport', VIEWPORT_CHANGED_EVENT, options);
}

/**
 * Requests the actual viewport safe area insets information.
 * @param options - request options.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {FunctionNotAvailableError} The function is not supported
 * @since Mini Apps v8.0
 * @example
 * if (requestSafeAreaInsets.isAvailable()) {
 *   const insets = await requestSafeAreaInsets();
 * }
 */
export const requestSafeAreaInsets = wrapSafe(
  'requestSafeAreaInsets',
  (options?: RequestOptionsNoCapture): AbortablePromise<EventPayload<'safe_area_changed'>> => {
    return request(REQUEST_SA_METHOD_NAME, SA_CHANGED_EVENT, options);
  },
);

import { isPageReload } from '@telegram-apps/navigation';
import { type EventListener, off, on } from '@telegram-apps/bridge';
import { AbortablePromise } from 'better-promises';

import { defineMountFn } from '@/scopes/defineMountFn.js';
import { $launchParams } from '@/globals.js';
import { signalCancel } from '@/scopes/signalCancel.js';
import type { RequestOptionsNoCapture } from '@/types.js';

import { wrapBasic } from './wrappers.js';
import {
  COMPONENT_NAME,
  CSA_CHANGED_EVENT,
  FS_CHANGED_EVENT,
  SA_CHANGED_EVENT,
  VIEWPORT_CHANGED_EVENT,
} from './const.js';
import { contentSafeAreaInsets, getStateFromStorage, safeAreaInsets, setState } from './signals.js';
import { requestContentSafeAreaInsets, requestSafeAreaInsets, requestViewport } from './static.js';

const onViewportChanged: EventListener<'viewport_changed'> = (data) => {
  const { height } = data;
  setState({
    isExpanded: data.is_expanded,
    height,
    width: data.width,
    stableHeight: data.is_state_stable ? height : undefined,
  });
};

const onFullscreenChanged: EventListener<'fullscreen_changed'> = (data) => {
  setState({ isFullscreen: data.is_fullscreen });
};

const onSafeAreaChanged: EventListener<'safe_area_changed'> = (data) => {
  setState({ safeAreaInsets: data });
};

const onContentSafeAreaChanged: EventListener<'content_safe_area_changed'> = (data) => {
  setState({ contentSafeAreaInsets: data });
};

const [
  fn,
  tMountPromise,
  tMountError,
  tIsMounted,
] = defineMountFn(
  COMPONENT_NAME,
  (options?: RequestOptionsNoCapture) => {
    // Try to restore the state using the storage.
    const s = isPageReload() && getStateFromStorage();
    return s
      ? AbortablePromise.resolve(s)
      : AbortablePromise.fn(async context => {
        // Request all insets.
        const insets = await AbortablePromise.all([
          requestSafeAreaInsets.isAvailable()
            ? requestSafeAreaInsets(context)
            : safeAreaInsets(),
          requestContentSafeAreaInsets.isAvailable()
            ? requestContentSafeAreaInsets(context)
            : contentSafeAreaInsets(),
        ]);

        const lp = $launchParams();
        const shared = {
          contentSafeAreaInsets: insets[1],
          isFullscreen: !!lp.tgWebAppFullscreen,
          safeAreaInsets: insets[0],
        };

        // If the platform has a stable viewport, it means we could use the window global object
        // properties.
        if (['macos', 'tdesktop', 'unigram', 'webk', 'weba', 'web'].includes(lp.tgWebAppPlatform)) {
          const w = window;
          return {
            ...shared,
            height: w.innerHeight,
            isExpanded: true,
            stableHeight: w.innerHeight,
            width: w.innerWidth,
          };
        }

        // We were unable to retrieve data locally. In this case, we are
        // sending a request returning the viewport information.
        return requestViewport(context).then(data => ({
          ...shared,
          height: data.height,
          isExpanded: data.is_expanded,
          stableHeight: data.is_state_stable ? data.height : 0,
          width: data.width,
        }));
      }, options);
  },
  (result) => {
    on(VIEWPORT_CHANGED_EVENT, onViewportChanged);
    on(FS_CHANGED_EVENT, onFullscreenChanged);
    on(SA_CHANGED_EVENT, onSafeAreaChanged);
    on(CSA_CHANGED_EVENT, onContentSafeAreaChanged);
    setState(result);
  },
);

/**
 * Mounts the Viewport component.
 * @throws {FunctionNotAvailableError} The environment is unknown
 * @throws {FunctionNotAvailableError} The SDK is not initialized
 * @throws {ConcurrentCallError} The component is already mounting
 * @example
 * if (mount.isAvailable() && !isMounting()) {
 *   await mount();
 * }
 */
export const mount = wrapBasic('mount', fn);
export const [, mountPromise, isMounting] = tMountPromise;
export const [, mountError] = tMountError;
export const [_isMounted, isMounted] = tIsMounted;

/**
 * Unmounts the Viewport.
 */
export function unmount(): void {
  signalCancel(mountPromise);
  off(VIEWPORT_CHANGED_EVENT, onViewportChanged);
  off(FS_CHANGED_EVENT, onFullscreenChanged);
  off(SA_CHANGED_EVENT, onSafeAreaChanged);
  off(CSA_CHANGED_EVENT, onContentSafeAreaChanged);
  _isMounted.set(false);
}

import { camelToKebab } from '@telegram-apps/toolkit';
import { deleteCssVar, setCssVar } from '@/utils/css-vars.js';

