import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { 
  Users, 
  BarChart3, 
  Home, 
  Bell,
  Settings,
  User
} from 'lucide-react';
import 'bootstrap/dist/css/bootstrap.min.css';

interface LayoutProps {
  children: React.ReactNode;
}

const Layout: React.FC<LayoutProps> = ({ children }) => {
  const location = useLocation();

  // --- СТЕЙТ ДЛЯ ЛОГИНА ---
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [loginData, setLoginData] = useState({ email: '', password: '' });
  const [loginError, setLoginError] = useState<string | null>(null);
  const [token, setToken] = useState<string | null>(null);

  // --- СТЕЙТ ДЛЯ МОДАЛКИ НАСТРОЕК ---
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [profileData, setProfileData] = useState({
    name: '',
    email: '',
    currentPassword: '',
    newPassword: '',
  });
  const [profileMessage, setProfileMessage] = useState<string | null>(null);
  const [profileLoading, setProfileLoading] = useState(false);

  // --- СТЕЙТ ДЛЯ УВЕДОМЛЕНИЙ ---
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState<any[]>([]);
  const [notificationsLoading, setNotificationsLoading] = useState(false);

  // --- Проверка токена при загрузке ---
  useEffect(() => {
    const savedToken = localStorage.getItem('access_token');
    if (savedToken) {
      setToken(savedToken);
      fetchProfile(savedToken);
    } else {
      setShowLoginModal(true);
    }
  }, []);

  // --- Получить профиль пользователя ---
  const fetchProfile = async (jwtToken: string) => {
    try {
      const res = await fetch('/api/user/profile', {
        headers: { Authorization: `Bearer ${jwtToken}` },
      });
      if (res.ok) {
        const data = await res.json();
        setProfileData({
          name: data.name,
          email: data.email,
          currentPassword: '',
          newPassword: '',
        });
      } else {
        setShowLoginModal(true);
      }
    } catch {
      setShowLoginModal(true);
    }
  };

  // --- Логин ---
  const handleLogin = async () => {
    setLoginError(null);
    try {
      const res = await fetch('/api/user/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(loginData),
      });
      if (res.ok) {
        const data = await res.json();
        setToken(data.access_token);
        localStorage.setItem('access_token', data.access_token);
        setShowLoginModal(false);
        fetchProfile(data.access_token);
      } else {
        setLoginError('Неверный email или пароль');
      }
    } catch {
      setLoginError('Ошибка авторизации');
    }
  };

  // --- Выход ---
  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('access_token');
    setShowLoginModal(true);
    setProfileData({ name: '', email: '', currentPassword: '', newPassword: '' });
  };

  // --- Сохранение профиля ---
  const handleProfileChange = (field: string, value: string) => {
    setProfileData(prev => ({ ...prev, [field]: value }));
  };

  const handleProfileSave = async () => {
    setProfileLoading(true);
    try {
      const res = await fetch('/api/user/profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          name: profileData.name,
          email: profileData.email,
          current_password: profileData.currentPassword,
          new_password: profileData.newPassword,
        }),
      });
      if (res.ok) {
        setProfileMessage('Профиль успешно обновлён');
        setTimeout(() => setProfileMessage(null), 3000);
        setShowProfileModal(false);
        fetchProfile(token!);
      } else {
        setProfileMessage('Ошибка обновления профиля');
        setTimeout(() => setProfileMessage(null), 3000);
      }
    } catch {
      setProfileMessage('Ошибка обновления профиля');
      setTimeout(() => setProfileMessage(null), 3000);
    } finally {
      setProfileLoading(false);
    }
  };

  // Загрузка уведомлений
  const fetchNotifications = async () => {
    if (!token) return;
    setNotificationsLoading(true);
    try {
      const res = await fetch('/api/notifications', {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (res.ok) {
        const data = await res.json();
        setNotifications(data);
      }
    } finally {
      setNotificationsLoading(false);
    }
  };

  // Открытие окна уведомлений
  const handleOpenNotifications = () => {
    setShowNotifications(true);
    fetchNotifications();
  };

  const navigation = [
    { name: 'Дашборд', href: '/', icon: Home },
    { name: 'Кандидаты', href: '/candidates', icon: Users },
    { name: 'Метрики', href: '/metrics', icon: BarChart3 },
  ];

  return (
    <div className="container-fluid h-100 p-0">
      <div className="row h-100 g-0 flex-nowrap">
        {/* Sidebar */}
        <nav className="col-12 col-md-3 col-lg-2 d-md-block bg-light sidebar collapse show p-0" style={{minHeight: '100vh'}}>
          <div className="position-sticky pt-3 d-flex flex-column h-100">
            {/* Logo */}
            <div className="mb-4 p-3 border-bottom d-flex align-items-center gap-3">
              <div className="bg-primary rounded-circle d-flex align-items-center justify-content-center" style={{width: 40, height: 40}}>
                <User className="text-white" />
              </div>
              <div>
                <h1 className="h5 mb-0">HR Admin</h1>
                <small className="text-muted">Панель управления</small>
              </div>
            </div>
            {/* Navigation */}
            <ul className="nav flex-column mb-4">
              {navigation.map((item) => {
                const isActive = location.pathname === item.href;
                return (
                  <li className="nav-item" key={item.name}>
                    <Link
                      to={item.href}
                      className={`nav-link d-flex align-items-center gap-2 ${isActive ? 'active fw-bold text-primary' : ''}`}
                    >
                      <item.icon />
                      {item.name}
                    </Link>
                  </li>
                );
              })}
            </ul>
            {/* User Profile */}
            <div className="mt-auto p-3 border-top">
              <div className="d-flex align-items-center gap-3">
                <div className="bg-primary rounded-circle d-flex align-items-center justify-content-center" style={{width: 40, height: 40}}>
                  <span className="text-white fw-bold">HR</span>
                </div>
                <div className="flex-grow-1">
                  <div className="fw-medium">{profileData.name}</div>
                  <div className="text-muted small">{profileData.email}</div>
                </div>
                <button className="btn btn-link p-0 text-secondary" onClick={() => setShowProfileModal(true)}>
                  <Settings />
                </button>
                <button className="btn btn-link p-0 text-danger" onClick={handleLogout}>
                  Выйти
                </button>
              </div>
            </div>
          </div>
        </nav>
        {/* Main Content */}
        <main className="col-12 col-md-9 col-lg-10 ms-sm-auto px-4 py-4 d-flex flex-column" style={{minHeight: '100vh'}}>
          {/* Top Bar */}
          <div className="d-flex flex-column flex-md-row align-items-start align-items-md-center justify-content-between mb-4 gap-3">
            <div>
              <h2 className="h4 mb-1">{navigation.find(item => item.href === location.pathname)?.name || 'HR Admin Panel'}</h2>
              <p className="text-muted mb-0">Управление HR процессами и кандидатами</p>
            </div>
            <div className="d-flex align-items-center gap-3">
              <button className="btn btn-outline-secondary position-relative" onClick={handleOpenNotifications}>
                <Bell />
                {notifications.some(n => !n.read) && (
                  <span className="position-absolute top-0 start-100 translate-middle p-1 bg-danger border border-light rounded-circle"></span>
                )}
              </button>
              <div className="vr mx-2 d-none d-md-block"></div>
              <div className="d-flex align-items-center gap-2">
                <div className="bg-primary rounded-circle d-flex align-items-center justify-content-center" style={{width: 32, height: 32}}>
                  <span className="text-white fw-bold">HR</span>
                </div>
                <div className="d-none d-md-block">
                  <div className="fw-medium">{profileData.name}</div>
                  <div className="text-muted small">{profileData.email}</div>
                </div>
              </div>
            </div>
          </div>
          {/* Page Content */}
          <div className="flex-grow-1 w-100">
            {children}
          </div>
        </main>
      </div>
      {/* Modals remain unchanged, but you can add Bootstrap modal classes if desired */}
      {/* Модальное окно логина */}
      {showLoginModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h2 className="text-subheaders text-background font-bold mb-4">Вход</h2>
            <div className="space-y-4">
              <input
                type="email"
                className="input-field"
                placeholder="Email"
                value={loginData.email}
                onChange={e => setLoginData({ ...loginData, email: e.target.value })}
              />
              <input
                type="password"
                className="input-field"
                placeholder="Пароль"
                value={loginData.password}
                onChange={e => setLoginData({ ...loginData, password: e.target.value })}
              />
              {loginError && <div className="text-error text-center">{loginError}</div>}
              <button className="btn btn-primary w-full" onClick={handleLogin}>
                Войти
              </button>
            </div>
          </div>
        </div>
      )}
      {/* Модальное окно уведомлений */}
      {showNotifications && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-lg max-h-[80vh] overflow-y-auto">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-subheaders text-background font-bold">Уведомления</h2>
              <button
                onClick={() => setShowNotifications(false)}
                className="text-background-2 hover:text-background"
              >
                <span style={{fontSize: 24, fontWeight: 700}}>×</span>
              </button>
            </div>
            {notificationsLoading ? (
              <div className="text-center text-main">Загрузка...</div>
            ) : notifications.length === 0 ? (
              <div className="text-center text-main">Нет уведомлений</div>
            ) : (
              <div className="space-y-4">
                {notifications.map((n) => (
                  <div key={n.id} className="p-4 rounded-lg bg-background-2">
                    <div className="text-main font-medium mb-1">{n.type}</div>
                    <div className="text-add mb-2">{n.message}</div>
                    <div className="text-time text-background-2">{new Date(n.created_at).toLocaleString()}</div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      )}
      {/* Модальное окно профиля */}
      {showProfileModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-subheaders text-background font-bold">Настройки профиля</h2>
              <button
                onClick={() => setShowProfileModal(false)}
                className="text-background-2 hover:text-background"
              >
                <span style={{fontSize: 24, fontWeight: 700}}>×</span>
              </button>
            </div>
            <div className="space-y-4">
              <div>
                <label className="block text-main text-background mb-2">Имя</label>
                <input
                  type="text"
                  value={profileData.name}
                  onChange={e => handleProfileChange('name', e.target.value)}
                  className="input-field"
                  placeholder="Ваше имя"
                />
              </div>
              <div>
                <label className="block text-main text-background mb-2">Email</label>
                <input
                  type="email"
                  value={profileData.email}
                  onChange={e => handleProfileChange('email', e.target.value)}
                  className="input-field"
                  placeholder="email@example.com"
                />
              </div>
              <div>
                <label className="block text-main text-background mb-2">Текущий пароль</label>
                <input
                  type="password"
                  value={profileData.currentPassword}
                  onChange={e => handleProfileChange('currentPassword', e.target.value)}
                  className="input-field"
                  placeholder="Введите текущий пароль"
                />
              </div>
              <div>
                <label className="block text-main text-background mb-2">Новый пароль</label>
                <input
                  type="password"
                  value={profileData.newPassword}
                  onChange={e => handleProfileChange('newPassword', e.target.value)}
                  className="input-field"
                  placeholder="Введите новый пароль"
                />
              </div>
            </div>
            <div className="flex gap-3 mt-6">
              <button
                onClick={handleProfileSave}
                className="btn btn-primary flex-1"
                disabled={profileLoading}
              >
                Сохранить
              </button>
              <button
                onClick={() => setShowProfileModal(false)}
                className="btn btn-outline flex-1"
                disabled={profileLoading}
              >
                Отмена
              </button>
            </div>
            {profileMessage && (
              <div className="mt-4 text-center text-main">{profileMessage}</div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Layout;
