      const resolve = withCleanup((result: Result) => {
        res(result);
        abort(withResolved(result));
      }) as PromiseResolveFn<Result>;
      reject = withCleanup(reason => {
        rej(reason);
        abort(reason);
      });

      //#region Process abortSignal option.
